//Referenced https://plainjs.com/javascript/utilities/set-cookie-get-cookie-and-delete-cookie-5/
const CookieManager = {
	
	//Referenced https://www.w3schools.com/js/js_cookies.asp
	getCookie(cname) {
		var name = cname + "=";
		var decodedCookie = decodeURIComponent(document.cookie);
		var ca = decodedCookie.split(';');
		for(var i = 0; i <ca.length; i++) {
			var c = ca[i];
			while (c.charAt(0) == ' ')
				c = c.substring(1);
			if (c.indexOf(name) == 0)
				return c.substring(name.length, c.length);
		}
	 return null;
	},
	
	setCookie(name, value, days) {
		var d = new Date;
		d.setTime(d.getTime() + 24*60*60*1000*days);
		alert(name + "=" + value + ";path=/;expires=" + d.toGMTString());
		document.cookie = name + "=" + value + ";path=/;expires=" + d.toGMTString();
	},
	
	deleteCookie(name) { 
		var d = new Date;
		d.setTime(d.getTime() + 24*60*60*1000*-1);
		document.cookie = name + "=" + ";path=/;expires=" + d.toGMTString(); }
};

export default CookieManager;
