import React, { Component } from 'react';
import Cookies from 'js-cookie';
import './App.css';

import { Document } from 'react-pdf';

//const dir = '../resources/sheets/';
/*
const fs = require('fs');
const songList = ['All The Things You Are', 'Beautiful Love'];

fs.readdir(dir, (err, files) => {
  files.forEach(file => {
    songList.push(file);
  });
});


const ListItem = ({ value }) => (
  <li>{value}</li>
);

const List = ({ items }) => (
  <ul>
    {
      items.map((item, i) => <ListItem key={i} value={item} />)
    }
  </ul>
);
*/

class Songs extends Component {
	
	state = {
    numPages: null,
    pageNumber: 1,
	}
	
	onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
	}
  
	getToken(){
	  alert(Cookies.get('mytoken')); 
	}
  


  render() {
	 
	const { pageNumber, numPages } = this.state;

    return (
      <div className="Songs">
        <h1> Songs page </h1>
        
		
		<div>
        <Document
          file="./sheets/Stablemates.pdf"
          onLoadSuccess={this.onDocumentLoadSuccess}
        >
          <Page pageNumber={pageNumber} />
        </Document>
        <p>Page {pageNumber} of {numPages}</p>
		</div>
		
		
		
		<button onClick={this.getToken}>
			getToken
		</button>
      </div>
	  
    );
  }
}

export default Songs;
