import React, { Component } from 'react';
import {Redirect} from 'react-router-dom';
import './App.css';
import Auth from './Auth';
import Cookies from 'js-cookie';


class Login extends Component {
  constructor() {
    super();
    this.state = {
      username: '',
      password: ''
    };

    this.handlePassChange = this.handlePassChange.bind(this);
    this.handleUserChange = this.handleUserChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async handleSubmit(evt) {
    evt.preventDefault();
	

        var data = {
            username: this.state.username,
            password: this.state.password
        }
		
        await fetch("http://localhost:4000/auth", {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(data)
        }).then(function(response) {
            if (response.status >= 400) {
              throw new Error("Bad response from server");
            }
            return response.json();
        }).then(function(response) {
			//Response from the verified /auth POST
			Cookies.set('access_token', response.token); //Create a cookie to expire in 2 weeks
			alert(Cookies.get('access_token'));
			//Get auth relies on requesting decrypting from back end-- back end responds w boolean
			
        }).catch(function(err) {
            console.log(err)
        });

  }
  
  getToken(){
	  alert(Cookies.get('access_token')); 
  }

  handleUserChange(evt) {
    this.setState({
      username: evt.target.value,
    });
  };

  handlePassChange(evt) {
    this.setState({
      password: evt.target.value,
    });
  }

  render() {

    return (
      <div className="Login">
        <form onSubmit={this.handleSubmit}>

          <label>User Name</label>
          <input type="text" data-test="username" value={this.state.username} onChange={this.handleUserChange} />

          <label>Password</label>
          <input type="password" data-test="password" value={this.state.password} onChange={this.handlePassChange} />

          <input type="submit" value="Log In" data-test="submit" />
        </form>
		<div>
		<button onClick={this.getToken}>
			getToken
		</button>

		</div>
		
		<div>
		<button onClick={Auth.signout()}>
			Logout
		</button>
		</div>
		
      </div>
	  
	  
    );
  }
}

export default Login;
