const Auth = {
		
	signout() {
		localStorage.clear();
	},
		
	async getAuth() {
		
	    fetch('http://localhost:4000/verify', {
			method: 'GET',
			headers: {
			'Authorization': 'Bearer ' + localStorage['acess_token']
				}	
		})
		.then(res => res.json())
		.then(data => {
			alert(data.authorizedData.username);
			return true;
			})
		.catch(err => { console.log(err)
			return false		});
	},
	
	getUser() {
		return this.username;
	}
};

export default Auth;
