const Auth = {
		
	signout() {
		localStorage.clear();
	},
		
	async getAuth() {
		var isAuth = false;
		
		const response = await fetch('http://localhost:4000/verify', {
			method: 'GET',
			headers: {
			'Authorization': 'Bearer ' + localStorage['acess_token']
				}	
		})
		.then(res => res.json())
		.then(data => {
			alert(data.authorizedData.username);
			})
		.catch(err => { console.log(err) });
		
		return false;
	},
	
	getUser() {
		return this.username;
	}
};

export default Auth;
