const Auth = {
    isAuthenticated: false,
	username: "",
	
    authenticate() {
		alert("checking for logged in");
		alert(localStorage['acess_token'])
		fetch('http://localhost:4000/verify', {
			method: 'GET',
			headers: {
			'Authorization': 'Bearer ' + localStorage['acess_token']
				}	
		})
		.then(res => res.json())
		.then(data => {
			this.username = data.authorizedData.username;
			this.isAuthenticated = true;
			alert("within token auth: " + this.isAuthenticated + this.username);
			})
		.catch(err => { console.log(err) })
	},
		
	signout() {
		this.isAuthenticated = false;
		this.username = "";
		localStorage.clear();
	},
		
	getAuth() {
		return this.isAuthenticated;
	},
	
	getUser() {
		return this.username;
	}
};

export default Auth;
