const Auth = {
		
	signout() {
		localStorage.clear();
	},
		
	getAuth() {
		isAuth = false;
		fetch('http://localhost:4000/verify', {
			method: 'GET',
			headers: {
			'Authorization': 'Bearer ' + localStorage['acess_token']
				}	
		})
		.then(res => res.json())
		.then(data => {
			alert(data.authorizedData.username);
			Auth.getAuth();
			})
		.catch(err => { console.log(err) });
	},
	
	getUser() {
		return this.username;
	}
};

export default Auth;
